<section class="min-h-screen flex items-center justify-center bg-gradient-to-b from-[#76aeb8] to-[#e9f5f9]  p-6">
    <div id="timer-container" class="bg-white p-8 rounded-lg shadow-xl max-w-lg w-full">
        <h2 class="text-2xl font-bold text-center mb-6 text-gray-800">Set Your Goals</h2>
        <div class="flex justify-center items-center space-x-4 mb-6">
            <input type="number" id="hours" min="0" max="23" class="w-24 p-3 text-center border border-gray-300 rounded-lg text-xl" placeholder="HH">
            <span class="text-3xl text-gray-500">:</span>
            <input type="number" id="minutes" min="0" max="59" class="w-24 p-3 text-center border border-gray-300 rounded-lg text-xl" placeholder="MM">
        </div>
        <div class="flex justify-center space-x-4 mb-6">
            <button id="setBtn" class="bg-blue-600 text-white px-8 py-3 rounded-lg shadow hover:bg-blue-700 focus:outline-none flex items-center space-x-2">
                <svg xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 8v4l3 3m6-3A9 9 0 113 12a9 9 0 0118 0z" />
                </svg>
                <span>Set</span>
            </button>
            <button id="startBtn" class="startBtn hidden bg-green-600 text-white px-8 py-3 rounded-lg shadow hover:bg-green-700 focus:outline-none items-center space-x-2">
                <svg xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 3v16l13-8-13-8z" />
                </svg>
                <span>Start</span>
            </button>
            <button id="stopBtn" class="stopBtn hidden bg-red-600 text-white px-8 py-3 rounded-lg shadow hover:bg-red-700 focus:outline-none items-center space-x-2">
                <svg xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12" />
                </svg>
                <span>Stop</span>
            </button>
            <button id="submitBtn" class="submitBtn hidden bg-purple-600 text-white px-8 py-3 rounded-lg shadow hover:bg-purple-700 focus:outline-none items-center space-x-2">
                <svg xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 13l4 4L19 7" />
                </svg>
                <span>Submit</span>
            </button>
        </div>
        <div id="timerDisplay" class="text-center text-4xl font-bold text-gray-800"></div>
    </div>
</section>

<!-- Dialog Box -->
<div id="confirmDialog" class="confirmDialog fixed inset-0 items-center justify-center bg-black bg-opacity-50 hidden">
    <div class="bg-white p-6 rounded-lg shadow-lg max-w-sm w-full">
        <h3 class="text-xl font-semibold text-center mb-4">Are you sure you want to submit?</h3>
        <div class="flex justify-around space-x-4">
            <button id="yesBtn" class="w-full bg-green-600 text-white px-4 py-2 rounded-lg text-lg hover:bg-green-700">Yes</button>
            <button id="noBtn" class="w-full bg-red-600 text-white px-4 py-2 rounded-lg text-lg hover:bg-red-700">No</button>
        </div>
    </div>
</div>

<script>
    document.querySelectorAll('.emoji-btn').forEach(button => {
    button.addEventListener('click', function() {
        // Tambahkan kelas Tailwind untuk memperbesar
        this.classList.add('scale-105');

        // Hapus kelas setelah animasi selesai (200ms, sesuai dengan durasi transisi)
        setTimeout(() => {
            this.classList.remove('scale-105');
        }, 250);
    });
});

document.addEventListener('DOMContentLoaded', function() {
    const emojiButtons = document.querySelectorAll('.emoji-btn');
    const feelingText = document.getElementById('feeling-text');
    const setBtn = document.getElementById('setBtn');
    const startBtn = document.getElementById('startBtn');
    const stopBtn = document.getElementById('stopBtn');
    const submitBtn = document.getElementById('submitBtn');
    const timerDisplay = document.getElementById('timerDisplay');
    const confirmDialog = document.getElementById('confirmDialog');
    const yesBtn = document.getElementById('yesBtn');
    const noBtn = document.getElementById('noBtn');
    const alertBox = document.getElementById('requestPermissionAlert');
    let timer;
    let timeLeft;

    emojiButtons.forEach(btn => {
        btn.addEventListener('click', function() {
            const emotion = this.dataset.emotion;
            feelingText.textContent = `saaya merasa ${emotion} hari ini!`;
            emojiButtons.forEach(b => b.classList.remove('selected'));
            this.classList.add('selected');
        });
    });

    setBtn.addEventListener('click', function() {
        const hours = parseInt(document.getElementById('hours').value) || 0;
        const minutes = parseInt(document.getElementById('minutes').value) || 0;
        timeLeft = hours * 3600 + minutes * 60;
        updateTimerDisplay();
        setBtn.classList.add('hidden');
        startBtn.classList.remove('hidden');
        submitBtn.classList.remove('hidden');
    });

    startBtn.addEventListener('click', function() {
        timer = setInterval(updateTimer, 1000);
        startBtn.classList.add('hidden');
        stopBtn.classList.remove('hidden');
    });

    stopBtn.addEventListener('click', function() {
        clearInterval(timer);
        stopBtn.classList.add('hidden');
        startBtn.classList.remove('hidden');
    });

    submitBtn.addEventListener('click', function() {
        confirmDialog.classList.remove('hidden');
    });

    yesBtn.addEventListener('click', function() {
        confirmDialog.classList.add('hidden');
        clearInterval(timer);
        startBtn.classList.add('hidden');
        stopBtn.classList.add('hidden');
        submitBtn.classList.add('hidden');
        showAlert();
    });

    noBtn.addEventListener('click', function() {
        confirmDialog.classList.add('hidden');
    });

    function updateTimer() {
        if (timeLeft > 0) {
            timeLeft--;
            updateTimerDisplay();
        } else {
            clearInterval(timer);
            timerDisplay.textContent = "Time's up!";
        }
    }

    function updateTimerDisplay() {
        const hours = Math.floor(timeLeft / 3600);
        const minutes = Math.floor((timeLeft % 3600) / 60);
        const seconds = timeLeft % 60;
        timerDisplay.textContent = `${padZero(hours)}:${padZero(minutes)}:${padZero(seconds)}`;
    }

    function padZero(num) {
        return num.toString().padStart(2, '0');
    }

    function showAlert() {
        alertBox.classList.remove('hidden');
        setTimeout(() => {
            alertBox.classList.add('hidden');
        }, 3000); // Alert will disappear after 3 seconds
    }
});
</script>
@endsection
